from django.urls import re_path
from rest_framework_simplejwt import views


# "JWT Tokens are stateless - meaning they don't get saved on the server in e.g. a session or the 
# database. The easiest way for your application would be to just delete the JWT token on the client 
# when the client makes the logout request" i.e. frontend responsible for deleting token
urlpatterns = [
    re_path(r"^jwt/create/?", views.TokenObtainPairView.as_view(), name="jwt-create"),
    re_path(r"^jwt/refresh/?", views.TokenRefreshView.as_view(), name="jwt-refresh"),
    re_path(r"^jwt/verify/?", views.TokenVerifyView.as_view(), name="jwt-verify"),
]
